import "./App.css";
import People from "./Components/People/People";
import { Component } from "react";
import 'tachyons';
import Clarifai from 'clarifai';



class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imgUrl: '',
    }
  }

  onClickHandler = () =>{
    console.log('click!');
    this.setState({input: this.state.imgUrl});

  }

  onInputChange = (event) =>{
    this.setState({input: event.target.value})
  }

  render() {
    return (
      <div>
        <People onClickHandler={this.onClickHandler} onInputChange={this.onInputChange}/>
      </div>
    );
  }
}



export default App;
