import "./App.css";
import { Component } from "react";
import "tachyons";
import Clarifai from "clarifai";
import Particles from "react-particles-js";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import ParticlesConfig from "./Components/Particles/ParticlesConfig";
import ActorImage from "./Components/ActorImage/ActorImage";
import DetectedActor from "./Components/DetectedActor/DetectedActor";

const particlesOption = {
  particles: {
    number: {
      value: 160,
      density: {
        enable: true,
        value_area: 800,
      },
    },
    color: {
      value: "#ffffff",
    },
    shape: {
      type: "circle",
      stroke: {
        width: 0,
        color: "#000000",
      },
      polygon: {
        nb_sides: 5,
      },
      image: {
        src: "img/github.svg",
        width: 100,
        height: 100,
      },
    },
    opacity: {
      value: 1,
      random: true,
      anim: {
        enable: true,
        speed: 1,
        opacity_min: 0,
        sync: false,
      },
    },
    size: {
      value: 3,
      random: true,
      anim: {
        enable: false,
        speed: 4,
        size_min: 0.3,
        sync: false,
      },
    },
    line_linked: {
      enable: false,
      distance: 150,
      color: "#ffffff",
      opacity: 0.4,
      width: 1,
    },
    move: {
      enable: true,
      speed: 1,
      direction: "none",
      random: true,
      straight: false,
      out_mode: "out",
      bounce: false,
      attract: {
        enable: false,
        rotateX: 600,
        rotateY: 600,
      },
    },
  },
  interactivity: {
    detect_on: "canvas",
    events: {
      onhover: {
        enable: true,
        mode: "bubble",
      },
      onclick: {
        enable: true,
        mode: "repulse",
      },
      resize: true,
    },
    modes: {
      grab: {
        distance: 400,
        line_linked: {
          opacity: 1,
        },
      },
      bubble: {
        distance: 250,
        size: 0,
        duration: 2,
        opacity: 0,
        speed: 3,
      },
      repulse: {
        distance: 400,
        duration: 0.4,
      },
      push: {
        particles_nb: 4,
      },
      remove: {
        particles_nb: 2,
      },
    },
  },
  retina_detect: true,
};

const app = new Clarifai.App({
  apiKey: "812fd1e20128438291b068e79b6ab402",
});

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imgUrl: "",
      detection: "",
    };
  }

  detectedImageHandler = (data) => {
    const detectedImage = data.outputs[0].data.regions[0].data.concepts[0].name;
    console.log(detectedImage);
  };

  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  };

  onClickHandler = () => {
    this.setState({ imgUrl: this.state.input });
    app.models.predict(Clarifai.CELEBRITY_MODEL, this.state.input).then(
      function (response) {
        this.detectedImageHandler(response);
      },
      function (err) {
        console.log(err);
      }
    );
  };

  render() {

    return (
      <div>
        <Particles params={particlesOption} className="particles" />
        <Logo />
        <ImageLinkForm
          onClickHandler={this.onClickHandler}
          onInputChange={this.onInputChange}
        />
        <ActorImage imgUrl={this.state.imgUrl} />
        <DetectedActor detection={this.state.detection} />
      </div>
    );
  }
}

export default App;
