import "./App.css";
import { Component } from "react";
import "tachyons";
import Clarifai from 'clarifai';
import Particles from "react-particles-js";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import ParticlesConfig from './Components/Particles/ParticlesConfig';
import ActorImage from './Components/ActorImage/ActorImage';


const app = new Clarifai.App({
  apiKey: '812fd1e20128438291b068e79b6ab402'
})

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imgUrl: "",
    };
  }

  onClickHandler = () => {
    console.log("click!");
    this.setState({ imgUrl: this.state.imgUrl });
  };

  onInputChange = (event) => {
    this.setState({ input: event.target.value });
    app.model.predict(Clarifai, '0676ebddd5d6413ebdaa101570295a39').then(
      function(response){
        console.log(response)
      },
      function(err){
        console.log(err);
      }
    ) 
  };

  render() {
    return (
      <div>
        <Particles params={ParticlesConfig} className="particles" />
        <Logo />
        <ImageLinkForm onClickHandler={this.onClickHandler} onInputChange={this.onInputChange}/>
        <ActorImage imgUrl={this.state.imgUrl}/>
      </div>
    );
  }
}

export default App;
