import "./App.css";
import { Component } from "react";
import "tachyons";
import Clarifai from "clarifai";
import Particles from "react-particles-js";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import ActorImage from "./Components/ActorImage/ActorImage";
//
import DetectedActor from "./Components/DetectedActor/DetectedActor";
import SignIn from "./Components/SignIn/SignIn";

const app = new Clarifai.App({
  apiKey: "812fd1e20128438291b068e79b6ab402",
});

const particlesConfig = {
  particles: {
    number: {
      value: 160,
      density: {
        enable: true,
        value_area: 800,
      },
    },
    color: {
      value: "#ffffff",
    },
    shape: {
      type: "circle",
      stroke: {
        width: 0,
        color: "#000000",
      },
      polygon: {
        nb_sides: 5,
      },
    },
    opacity: {
      value: 1,
      random: true,
      anim: {
        enable: true,
        speed: 1,
        opacity_min: 0,
        sync: false,
      },
    },
    size: {
      value: 3,
      random: true,
      anim: {
        enable: false,
        speed: 4,
        size_min: 0.3,
        sync: false,
      },
    },
    line_linked: {
      enable: false,
      distance: 150,
      color: "#ffffff",
      opacity: 0.4,
      width: 1,
    },
    move: {
      enable: true,
      speed: 1,
      direction: "none",
      random: true,
      straight: false,
      out_mode: "out",
      bounce: false,
      attract: {
        enable: false,
        rotateX: 600,
        rotateY: 600,
      },
    },
  },
  retina_detect: false,
};





class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imgUrl: "",
      nameDetected: '',
    };
  }

  detectedImageHandler = (data) => {   
    const detectedImage = data.outputs[0].data.regions[0].data.concepts[0].name;
    const arrImage = detectedImage.split(' ');
    const Name = [];
    arrImage.forEach(word =>{
      if(word.includes('.')){
        Name.push(word.toUpperCase())
      } else if (word[0] === word[0].toLowerCase()){
        Name.push(word[0].toUpperCase() + word.substr(1).toLowerCase());
      }
    })

    return Name.join(' '); 
  };

  displayName = (actorName) => {
    this.setState({ nameDetected: actorName });
  };

  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  };

  onClickHandler = () => {
    this.setState({ imgUrl: this.state.input });
    app.models
    .predict(Clarifai.CELEBRITY_MODEL, this.state.input)
    .then(response => this.displayName(this.detectedImageHandler(response)))
    .catch(err => console.log(err));
  };



  render() {
    <Particles params={particlesConfig} className="particles" />
    return (
      <div>
        <SignIn />

        <Logo />
        <ImageLinkForm
          onClickHandler={this.onClickHandler}
          onInputChange={this.onInputChange}
        />
        <ActorImage imgUrl={this.state.imgUrl} nameDetected={this.state.nameDetected} />
        <DetectedActor className="center" />
      </div>
    );
  }
}

export default App;
