import "./App.css";
import { Component } from "react";
import "tachyons";
import Clarifai from "clarifai";
import Particles from "react-particles-js";
import Logo from "./Components/Logo/Logo";
import ImageLinkForm from "./Components/ImageLinkForm/ImageLinkForm";
import ParticlesConfig from "./Components/Particles/ParticlesConfig";
import ActorImage from "./Components/ActorImage/ActorImage";
import DetectedActor from './Components/DetectedActor/DetectedActor';

const app = new Clarifai.App({
  apiKey: "812fd1e20128438291b068e79b6ab402",
});

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imgUrl: "",
      detection: ""
    };
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  };

  onClickHandler = () => {
    this.setState({ imgUrl: this.state.input });
    app.models.predict(Clarifai.CELEBRITY_MODEL, this.state.input).then(
      function (response) {
        console.log(response.outputs[0].data.regions[0].data.concepts[0].name)
        this.setState({detection: response.outputs[0].data.regions[0].data.concepts[0].name}) 
      },
      function (err) {
        console.log(err);
      }
    );
  };



  render() {
    return (
      <div>
        <Particles params={ParticlesConfig} className="particles" />
        <Logo />
        <ImageLinkForm
          onClickHandler={this.onClickHandler}
          onInputChange={this.onInputChange}
        />
        <ActorImage imgUrl={this.state.imgUrl} />
        <DetectedActor detection={this.state.detection}/>
      </div>
    );
  }
}

export default App;
